// src/pages/ProjectBoard.jsx
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../utils/api";
import "../styles/board.css";
import Navbar from "../components/Navbar";

function ProjectBoard() {
  const { id } = useParams();
  const [project, setProject] = useState(null);
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    API.get(`/projects/${id}/`)
      .then(res => {
        setProject(res.data);
        setTasks(res.data.tasks || []);
      })
      .catch(err => {
        console.error(err);
        alert("Error loading project");
        navigate("/");
      });
  }, [id, navigate]);

  const handleCreateTask = () => navigate(`/project/${id}/create-task`);

  return (
    <div className="container">
      <Navbar />
      <h2>{project?.name}</h2>
      <p>{project?.description}</p>
      <button onClick={handleCreateTask} className="create-btn">
        + New Task
      </button>
      <div className="board">
        {["TODO", "INPROGRESS", "DONE"].map((statusKey) => (
          <div key={statusKey} className="column">
            <h3>{
              statusKey === "TODO" ? "To-Do" :
              statusKey === "INPROGRESS" ? "In-Progress" : "Done"
            }</h3>
            {tasks.filter(t => t.status === statusKey).map(task => (
              <div key={task.id} className="task"
                   onClick={() => navigate(`/task/${task.id}`)}>
                {task.title}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProjectBoard;
