import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import API from "../utils/api";
import "../styles/task.css";

function TaskDetail() {
  const { id } = useParams();
  const [task, setTask] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const taskRes = await API.get(`/tasks/${id}/`);
        const commentRes = await API.get(`/tasks/${id}/comments/`);
        setTask(taskRes.data);
        setComments(commentRes.data);
      } catch (err) {
        alert("Failed to load task or comments");
      }
    };
    fetchData();
  }, [id]);

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;
    try {
      const res = await API.post(`/tasks/${id}/comments/`, { content: newComment });
      setComments([...comments, res.data]);
      setNewComment("");
    } catch (err) {
      alert("Failed to post comment");
    }
  };

  return (
    <div className="container">
      <h2>Task Details</h2>
      {task && (
        <div className="task-info">
          <h3>{task.title}</h3>
          <p>{task.description}</p>
          <p><strong>Status:</strong> {task.status}</p>
          <p><strong>Assigned to:</strong> {task.assigned_to_username || "Unassigned"}</p>
          <p><strong>Due date:</strong> {task.due_date || "Not set"}</p>
        </div>
      )}
      <div className="comments">
        <h3>Comments</h3>
        {comments.length === 0 ? <p>No comments yet.</p> : (
          <ul>
            {comments.map((c) => (
              <li key={c.id}>
                <strong>{c.user_username}</strong>: {c.content} <em>({new Date(c.timestamp).toLocaleString()})</em>
              </li>
            ))}
          </ul>
        )}
        <form onSubmit={handleAddComment}>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Add a comment..."
            rows="3"
          />
          <br />
          <button type="submit">Post Comment</button>
        </form>
      </div>
    </div>
  );
}

export default TaskDetail;
