import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import ProjectBoard from "./pages/ProjectBoard";
import TaskDetail from "./pages/TaskDetail";
import CreateProject from "./pages/CreateProject";
import CreateTask from "./pages/CreateTask";

function App() {
  const isAuthenticated = !!localStorage.getItem("access_token");

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/" element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/create-project" element={isAuthenticated ? <CreateProject /> : <Navigate to="/login" />} />
        <Route path="/project/:id" element={isAuthenticated ? <ProjectBoard /> : <Navigate to="/login" />} />
        <Route path="/task/:id" element={isAuthenticated ? <TaskDetail /> : <Navigate to="/login" />} />
        <Route path="/project/:id/create-task" element={isAuthenticated ? <CreateTask /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
