import React, { useState } from "react";
import API from "../utils/api";
import { useNavigate } from "react-router-dom";
import "../styles/project.css";

function CreateProject() {
  const [form, setForm] = useState({ name: "", description: "" });
  const [emails, setEmails] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/projects/create/", form);
      const projectId = res.data.id;
      const emailList = emails.split(",").map(e => e.trim()).filter(e => e);
      for (let email of emailList) {
        await API.post(`/projects/${projectId}/add-member/`, { username_or_email: email });
      }
      alert("Project created!");
      navigate(`/project/${projectId}`);
    } catch (err) {
      console.error("Project creation failed:", err);
      alert("Failed to create project");
    }
  };

  return (
    <div className="container">
      <h2>Create New Project</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Project Name" onChange={handleChange} required />
        <textarea name="description" placeholder="Project Description" onChange={handleChange} rows="3" />
        <input placeholder="Add member emails (comma-separated)" onChange={(e) => setEmails(e.target.value)} />
        <button type="submit">Create Project</button>
      </form>
    </div>
  );
}

export default CreateProject;
